export default (posts = [], action) => {
    switch (action.type){
        case 'FETCH_ALL':
            return action.payload;
        break;
        case 'CREATE':
            return [...posts, action.payload];
        break;
        case 'UPDATE':
            return posts.map((post)=> post.id == action.payload._id ? action.payload : post);
        break;
        case 'UPDATE':
            return posts.map((post)=> post.id == action.payload._id ? action.payload : post);
        break;
        default:
            return posts;
        
    }
    // if(action.type == 'CREATE'){

